class SplitText { constructor(t, { type: e = "chars", containerType: s = "span" } = {}) { this.element = t, this.type = e, this.containerType = s, this.checkType() } checkType() { let { type: t } = this; switch (t) { case "chars": this.splitContentHandler(), this.setNewElementContentChars(); break; case "words": this.splitWordsHandler(), this.setNewElementContentWords(); break; default: console.log("-!!- SplitText ---- Wrong Type -!!-") } } setNewElementContentChars() { let { element: t, chars: e, containerType: s } = this; t.innerHTML = e.map(t => `<${s}>${t}</${s}>`).join(""), this.chars = t.querySelectorAll(`${s}`) } setNewElementContentWords() { let { element: t, chars: e, containerType: s } = this, n = e.map(t => `<${s}>${t}</${s}> `).join(""); t.innerHTML = n, this.words = t.querySelectorAll(`${s}`) } splitContentHandler() { let { element: t } = this; this.chars = t.textContent.split("") } splitWordsHandler() { let { element: t } = this, e = t.textContent.split(" "); this.chars = e.filter(t => "" !== t) } }